import React from "react";
import axios from "axios";
import MovieCard from "./MovieCard";

let sampleMovie = [
  { id: "" },
  { title: "" },
  { director: "" },
  { year: "" },
  { posterUrl: "" }
];

class GetMovie extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: sampleMovie,
      answers: [sampleMovie, sampleMovie, sampleMovie, sampleMovie],
      answer1: sampleMovie,
      answer2: sampleMovie,
      answer3: sampleMovie,
      answer4: sampleMovie,
      goodAnswer: 0
    };
  }
  componentDidMount() {
    this.getMovie();
    this.getAnswers();
  }
  getMovie() {
    axios
      .get("https://hackathon-wild-hackoween.herokuapp.com/movies")
      // Extract the DATA from the received response
      .then(response => response.data)
      // Use this data to update the state
      .then(data => {
        this.setState({
          movies: data.movies[Math.floor(Math.random() * Math.floor(82))]
        });
      });
    console.log(this.state.answers);
  }

  getAnswers() {
    let movie1 = Math.floor(Math.random() * Math.floor(82));
    let movie2 = Math.floor(Math.random() * Math.floor(82));
    let movie3 = Math.floor(Math.random() * Math.floor(82));
    let movie4 = Math.floor(Math.random() * Math.floor(82));
    let goodAnswer = Math.floor(Math.random() * Math.floor(3)) + 1;
    axios
      .get("https://hackathon-wild-hackoween.herokuapp.com/movies")
      // Extract the DATA from the received response
      .then(response => response.data)
      // Use this data to update the state
      .then(data => {
        this.setState({
          answer1: data.movies[movie1],
          answer2: data.movies[movie2],
          answer3: data.movies[movie3],
          answer4: data.movies[movie4]
        });
        this.setState({ goodAnswer });
      });
  }

  render() {
    return (
      <div>
        <MovieCard
          posterUrl={this.state.movies.posterUrl}
          title={this.state.movies.title}
        ></MovieCard>
        <button>{this.state.answer1.title}`</button>
        <button>{this.state.answer2.title}</button>
        <button>{this.state.answer3.title}</button>
        <button>{this.state.answer4.title}</button>
      </div>
    );
  }
}
export default GetMovie;
